class Solution {
public:
    int maxOperations(string s) 
    {
        int cnt=0, ans=0, flag=1;

        for(char ch: s)
        {
            if(ch=='1')
            {
                cnt++;
                flag=1;
            }
            else
            {
                if(flag)
                ans+=cnt;

                flag=0;
            }
        }

        return ans;
    }
};
