#define rep(i,a,b) for(int i=a;i<b;i++)
#define sz(c) (int)(c).size()

class Solution {
public:
    int maxOperations(string s) {
        int n=sz(s);
        int res=0;
        vector<int> ipos;
        int st=0;
        while(st<n) {
            if(s[st]=='0') {
                st++;
                continue;
            }
            int cnt=0;
            while(st<n && s[st]=='1') {
                cnt++;
                st++;
            }
            ipos.push_back(cnt);
        }
        int itr=0;
        while(itr < sz(ipos)) {
            if(itr==sz(ipos)-1) {
                if(s[n-1]=='0') res += ipos[itr];
                break;
            }
            res += ipos[itr];
            ipos[itr+1]+=ipos[itr];
            itr++;
        }
        return res;
    }
};
