class Solution {
public:
    int maxOperations(string s) {
        vector<int> blocks;
        bool one = false;
        for(auto c : s) {
            if(c == '1') {
                if(one) {
                    blocks.back()++;
                } else {
                    blocks.push_back(1);
                }
                one = true;
            } else {
                one = false;
            }
        }
        if(one) blocks.pop_back();
        int sum = 0, ans = 0;
        for(auto v : blocks) {
            sum += v;
            ans += sum;
        }
        return ans;
    }
};
