class Solution {
public:
    int maxOperations(string s) {
        char b = '1';
        int zs = 0;
        int rt = 0;
        for ( int i = s.size() - 1; 0 <= i; --i ) {
            if ( s[i] == '1' ) {
                rt += zs;
            } else {
                if ( b == '1' ) ++zs;
            }
            b = s[i];
        }
        return rt;
    }
};
