class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int z = 0;
        for (int i = 0; i < n;) {
            if (s[i] == '1') {
                z++;
                i++;
                continue;
            }
            ans += z;
            while (i < n && s[i] == '0') {
                i++;
            }

        }
        return ans;
    }
};
