class Solution {
public:
    int maxOperations(string s) {

        int mohit = 0;
        int temp = 0;

        bool flag = false;

        vector<int> ArrBits;

        for(int i=0;i<s.size();i++){

            char ch = s[i];

            if (ch == '1') {
                flag = true;
                ArrBits.push_back(1);
            } 
            else if (ch == '0' && flag) {
                flag = false;
                ArrBits.push_back(0);
            }
        }

        for (int i=0;i<ArrBits.size();i++) {
            int num=ArrBits[i];
            if (num == 1) {
                temp += 1;
            } 
            else {
                mohit += temp;
            }
        }

        return mohit;
    }
};
