class Solution {
public:
    int maxOperations(string s) {
        string v="";
        bool one=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                v+=s[i];
                one=true;
            }
            else if(s[i]=='0' && one==true){
                v+=s[i];
                one=false;
            }
        }
        int c=0;
        int x=0;
        for(int i=0;i<v.size();i++){
            if(v[i]=='1')x++;
            else c+=x;
        }
        return c;

    }
};
