class Solution {
public:
    int maxOperations(string s) {
        int flip= 0;
         int zeroCnt  = 0;
        int n=s.length();
        for(int i=0; i<n; i++){
            if(s[i]=='1'){
                zeroCnt++;
            }
            if(s[i]=='0'&&s[i+1]=='1'){
                flip+=zeroCnt;
            }
            else if(s[i]=='0'&&i==n-1){
                flip+=zeroCnt;
            }
        }
        return flip;
    }
};
