class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        l = len(s)
        count = 0
        last = '0'
        for i in range(l):
            if s[i] == '1':
                count += 1
            else:
                if last == '1':
                    ans += count
            last = s[i]

        return ans
