class Solution {
public:
    int maxOperations(string s) {
        vector<int> part;
        int count = 0;
        for (auto c : s) {
            if (c == '1')
                count++;
            if (c == '0' && count > 0){
                part.push_back(count);
                count = 0;
            }
        }
        int ans = 0, acc = 0;
        for (auto i : part) {
            acc += i;
            ans += acc;
        }
        return ans;
    }
};
