class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        s += '1';
        int n = s.size(), ans = 0;
        for(int i=0; i<n-1; i++){
            if(s[i] == '1') count++;
            if(s[i] == '0' && s[i+1] == '1'){
                ans += count;
            }
        }

        return ans;
    }
};
