class Solution {
public:
    int maxOperations(string s) {
        int count1 = 0;
        bool previousZero = false;
        int ans = 0;

        for(int i=0; i<s.length(); i++)
        {
            if(s[i] == '0' && previousZero == false)
            {
                ans += count1;
                previousZero = true;
            }
            else if(s[i] == '1')
            {
                previousZero = false;
                count1++;
            }
        }

        return ans;
    }
};
