class Solution {
public:
    int maxOperations(string s) {
        int count=0,count2=0,ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                count++;
                count2=0;
            }
            if(s[i]=='0'){
                count2++;
               if(count2==1) ans+=count;

            }
        }
        return ans;
    }
};
