class Solution {
    public int maxOperations(String s) {
        int ans=0;
        int one=0;
        boolean flag = false;
        for(char a : s.toCharArray()){
            if(flag){
                if(a=='1'){
                    ans+=one;
                    flag=false;
                }
            }
            if(a=='0') flag=true;
            else if(a=='1') one++;
        }
        if(s.charAt(s.length()-1)=='0') ans+=one;
        return ans;
    }
}
