class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int c = 0;
        int f = 0;
        int ans = 0;
        for(int i =0 ; i < n; i++){
            if(s[i] == '1'){
                f = 1;
                c++;
            }else{
                if(f == 1){
                    f = 0;
                    ans += c;
                }
            }

        }
        return ans;
    }
};
