class Solution {
    public int maxOperations(String s) {
        int waitingOnes = 0;
        char prev = 'A';
        int ans = 0;
        for(char ch: s.toCharArray()){
            if(ch == '0'){
                if(prev == '1'){
                    ans += waitingOnes;
                }
            }
            else if(ch == '1'){
                waitingOnes++;
            }
            prev=  ch;
        }
        return ans;
    }
}
