class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int one = 0;
        int ans = 0;

        for(int i = 0; i < n; i++) {
            if(s[i] == '1') {
                one++;
                cout<<one<<" ";
            } else if(s[i] == '0') 
            {
                 while(s[i]=='0'&&i<n)
               {

                    i++;
                }
                i--;
                 cout<<one<<" ";
                ans += one;
            }
        }

        return ans;
    }
};
