class Solution:
    def maxOperations(self, s: str) -> int:
        r = s[::-1]
        mul = 0
        if r[0] == '0':
            mul = 1
        p = r[0]
        res = 0
        for c in r[1:]:
            if c == '0' and p == '1':
                mul += 1
            elif c == '1':
                res += mul
            p = c

        return res
