class Solution {
    public int maxOperations(String ss) {
        char[] s = ss.toCharArray();
        int ans = 0;
        int count = 0, prevCount = 0;
        for (int i = s.length - 2; i >= 0; i--) {
            if (s[i] == '0') {
                continue;
            }
            if (s[i + 1] == '0') {
                count++;
            }
            ans += count + prevCount;
            prevCount += count;
            count = 0;
        }
        return ans;
    }
}

