class Solution:
    def maxOperations(self, s: str) -> int:
        pre = 100010;
        precnt = 0;
        cnt = 0;
        res = 0;
        for i in range(len(s)-1,-1,-1):
            if i+1<len(s):
                if s[i]=='1':
                    if s[i+1]=='0':
                        cnt = precnt +1;
                    else:
                        cnt = precnt;
                    res = res + cnt;
                    precnt = cnt;
        return res;
