class Solution {
public:
    int maxOperations(string s) {
        int c=0;
        int n = s.length();

        int i=0;
        int ans=0;
        while(i<n)
        {
            while(i<n && s[i]=='0') i++;

            while(i<n && s[i]=='1')
            {
                c++;
                i++;
            }
            if(i<n && s[i]=='0')
            ans+=c;
        }

        return ans;
    }
};
