class Solution {
public:
    int maxOperations(string s) {
        ios::sync_with_stdio(false); 
        cin.tie(nullptr);
        int result=0;
        int p=0;
        int r=0;
        int c=0; 
        bool k=false; 
        vector<int> h; 
        for(int i=0;i<s.size();i++){ 
            if(s[i]=='1'){ 
                h.push_back(1); 
                k=true; 
                c++;
            } 
            if(s[i]=='0' and k==true){ 
                h.push_back(0); 
                k=false; 
                c--;
            } 
            cout << c << s[i] << endl;
        } 
        for(auto it:h){ 
            if(it==1){ 
                p++; 
            } 
            else 
            { 
                result+=p; 
            } 
        } 
        return result; 
    }
};
