class Solution {
public:
    int maxOperations(string s) {
        vector<int>v(s.length(),0);
        int count=0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1') count++;
            v[i]=count;
        }
        int ans=0;
    // for(int i=0;i<v.size();i++){
    //     cout<<v[i]<<" ";
    // }

        for(int i=s.length()-1;i>=1;i--){
            if(s[i]=='0' && s[i-1]=='1'){
                // cout<<i<<" ";
                ans+=v[i];
            }
        }
        return ans;
    }
};
