class Solution {
public:
    int maxOperations(string s) {
        int c = 0, res = 0, n = s.size();
        bool f = true;
        for(int i = 0; i < n; i++){
            if(s[i] == '0'){
                if(f){
                    res += c;                    
                }
                f = false;
            }
            else{
                f = true;
                c++;
            }
        }
        return res;
    }
};
