class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int cnt=0;
        if(s[0]=='1') cnt++;
        for(int i=1;i<n;i++){
            if(s[i]=='1') cnt++;
            else if(s[i-1]=='0') continue;
            else{
                ans+=cnt;
            }
        }
        return ans;
    }
};
