class Solution {
    public int maxOperations(String s) {
        int prefix[] = new int[s.length()];
        int count = 0 , ans = 0;
        for(int i = 0 ; i < s.length(); i++){
            if(s.charAt(i) == '1') count++;
            prefix[i] = count;
        }
        for(int i = 0 ; i < s.length()-1; i++){
            if(s.charAt(i) == '1' && s.charAt(i+1)=='0'){
                ans += prefix[i+1];
            }
        }
        return ans;
    }
}
