class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        vector<int> str;
        for(char c : s){
            if(c == '1'){
               str.push_back(1);
               cnt = 1;
            }
            else if(cnt == 1){
                str.push_back(0);
                cnt = 0;
            }
        }
        cnt = 0;
        for(int x : str){
            if(x) cnt++;
            else ans += cnt;
        }
        return ans;
    }
};
