class Solution {
public:
    int maxOperations(string s) {
        char prev=s[0];
        vector<int>a;int ct=0;
        for(int i=0;i<s.size();i++){
            if(s[i]==prev && prev=='1'){
                ct++;
            }
            else if(s[i]==prev && prev=='0'){ct=0;continue;}
            else if(s[i]!=prev){
                if(prev=='1'){
                    a.push_back(ct);
                    prev=s[i];ct=0;
                }else{
                    prev=s[i];ct=1;
                }

            }
            // cout<<i<<" "<<ct<<endl;
        }
        // if(s[s.size()-1]=='1')a.pop_back();
        // for(int x:a)cout<<x<<" ";
        int ans=0;int cum=0;
        for(int i=0;i<a.size();i++){
            cum+=a[i];
            ans+=cum;
        }
        return ans;
    }
};
