class Solution {
public:
int maxOperations(string s) {
    //find the num of 1 in the right
    //101101
    // 1 0
    //10 1
    //101 1
    //1011 1
    //10110 1+3
    int num1=0,res=0;
    bool b=true;
    for(int i=0;i<s.size();i++){
        if(s[i]=='1'){
            num1++;
            b=true;
        }else{
            if(b)
            res+=num1;
            b=false;
        }
    }
    return res;
}
};
