class Solution:
    def maxOperations(self, s: str) -> int:
        z = []
        l,r = 0,0
        ret = 0
        while l<len(s):
            if s[l]!='0':
                l+=1
            else:
                r = l
                while r<len(s):
                    if s[r]=='0':
                        r+=1
                    else:
                        z.append(l)
                        l = r
                        break
                if r==len(s):
                    z.append(l)
                    break
        i = 0
        def find(x,y):
            l,r = 0,len(y)-1
            while l <= r:
                mid = int((l+r)/2)
                if y[mid]>x:
                    r = mid-1
                else:
                    l = mid+1
            if l < len(y):
                return len(y)-l
            else:
                return 0
        while i < len(s):
            if s[i]=='1':
                r = find(i,z)
                ret += r
            i += 1
        return ret
