class Solution {
public:
    vector<int> processString(const string& str) {
        vector<int> arr;
        bool foundOne = false;

        for (char c : str) {
            if (c == '1') {
                arr.push_back(1);
                foundOne = true;
            } else if (c == '0' && foundOne) {
                arr.push_back(0);
                foundOne = false;
            }
        }

        return arr;
    }

    int calculateMaxOps(const vector<int>& bits) {
        int result = 0;
        int count = 0;

        for (int val : bits) {
            if (val == 1) {
                ++count;
            } else {
                result += count;
            }
        }

        return result;
    }

    int maxOperations(string s) {
        vector<int> bits = processString(s);
        return calculateMaxOps(bits);
    }
};
