class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, p = 0;
        bool has_one = false;
        for (char c : s) {
            if (c == '1') {
                has_one = true;
                p++;
            } else if (has_one) {
                ans += p;
                has_one = false;
            }
        }
        return ans;
    }
};
