class Solution {
public:
#define INTEGER int
#define BOOL bool
#define STRING string
#define VECTOR vector

INTEGER maxOperations(STRING s) {
    INTEGER result = 0, onesCount = 0;
    BOOL wasPreviousOne = true;

    for (INTEGER index = 0; index < s.size(); ++index) {
        if (s[index] == '1') {
            ++onesCount;
            wasPreviousOne = true;
        } else {
            if (wasPreviousOne) {
                result += onesCount;
            }
            wasPreviousOne = false;
        }
    }
    return result;
}
};

