class Solution {
    public int maxOperations(String S) {
        char s[] = S.toCharArray();
        int ans = 0;
        int n = s.length;
        int count = 0;
        int i = 0;
        while(i < n){
            if(s[i] == '1'){
                count++;
                i++;
            } else{
                int j = i;
                ans += count;
                while(j < n && s[j] == s[i])
                    j++;
                i = j;
            }
        }
        return ans;
    }
}
