class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, count = 0;
        for(int i=0;i<s.length();i++) {
            if(s[i] == '1') count++;
            else if(i>0&&s[i] == '0' &&s[i-1] == '1') {
                ans+=count;
            }
        }
        return ans;
    }
};
