class Solution {
public:
    int maxOperations(string s) {
        vector<int> processedBits = processString(s);
        return calculateMaxOperations(processedBits);
    }

private:
    vector<int> processString(const string& s) {
        vector<int> bits;
        bool inSequence = false;

        for (char ch : s) {
            if (ch == '1') {
                bits.push_back(1);
                inSequence = true;
            } else if (ch == '0' && inSequence) {
                bits.push_back(0);
                inSequence = false;
            }
        }

        return bits;
    }

    int calculateMaxOperations(const vector<int>& bits) {
        int maxOps = 0;
        int countOnes = 0;

        for (int bit : bits) {
            if (bit == 1) {
                countOnes++;
            } else {
                maxOps += countOnes;
            }
        }

        return maxOps;
    }
};
