class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int count = 0;
        bool active = false;
        for (int i = 0; i <= s.size(); ++i) {
            if (s[i] == '0') {
                active = true;
                continue;
            } else {
                if (active) {
                    res += count;
                    active = false;
                }
                count++;
            }
        }
        if (active) {
            res += count;
            active = false;
        }
        return res;
    }
};
