class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int res = 0;
        int c1 = 0;
        for(int i = 0; i < n; i ++) {
            if(s[i] == '1') c1 ++;
            if(s[i] == '0') {
                while(s[i] == '0') i ++;
                i --;
                res += c1;
            }
        }
        return res;
    }
};
