class Solution:
  def maxOperations(self, s: str) -> int:
    zero = '0'
    one = '1'
    n = len(s)
    one_count = 0
    i = 0
    ret = 0
    while i < n:
      if s[i] == zero:
        if i > 0 and s[i - 1] == one:
          ret += one_count
        i += 1
      else:
        while i < n and s[i] == one:
          one_count += 1
          i += 1
    return ret
