class Solution {
public:
    int maxOperations(string s) {
        int num_1 = 0;
        int ans = 0;
        int i = 0;
        while (i < s.length()) {
            if (s[i] == '1') {
                num_1 ++;
                i++;
            }
            else {
                ans += num_1;
                while (i < s.length() && s[i] == '0') {
                    i++;
                }
            }
        }
        return ans;
    }
};
