class Solution {
public:
    long long maxOperations(string s) {
        return come_On_Prahlad(s);
    }

private:
    long long come_On_Prahlad(string& s) {
        long long ans = 0, cnt = 0;
        bool flag = false;


        for(auto &i:s)
        {
            if(i=='0' && flag==true)
            {
                ans+=cnt;
                flag=false;
            }

            else if(i=='1')
            {
                cnt++;
                flag=true;
            }

        }

    return ans;
    }
};
