class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int one_cnt = 0, pre = 0;
        for(int i=0; i<s.length(); ++i){
            if( s[i] == '1' ){
                ++one_cnt;
            }else if( pre == 1 ){
                ans += one_cnt;
            }
            pre = s[i] - '0';
        }
        return ans;
    }
};
