class Solution {
public:
    int maxOperations(string s) {
        vector<int>qt;
        for(int i = 0 ; i < s.size() ; i ++)
        {
            int cnt = 0;
            while(s[i] == '1')
            {
                cnt ++;
                i ++;
            }
            if(cnt)i --;
            if(cnt)qt.push_back(cnt);
        }
        int n;
        int sum = 0;
        if(s[s.size() - 1] != '1')n = qt.size() - 1;
        else n = qt.size() - 2;
        for(int i = 1 ; i < qt.size() ; i ++)
        qt[i] += qt[i - 1];
        for(int i = 0 ; i <= n ; i ++)
        sum += qt[i];
        return sum;
    }
};
