class Solution {
public:
    int maxOperations(string s) {
        int bawa = 0;
        int ans = 0;
        vector<int> sim;
        for(int i = 0; i < s.length(); i++){
            if(s[i] == '1'){
                bawa++;
            }else{
                if(bawa != 0){
                    sim.push_back(bawa);
                }
                bawa = 0;
            }
        }
        if(bawa != 0){
            sim.push_back(bawa);
        }

        bawa = 0;
        for(int i = 0; i < sim.size(); i++){
            ans = ans + bawa;
            bawa = bawa + sim[i];
        }
        if(s[s.length() - 1] == '0'){
            ans = ans + bawa;
        }
        return ans;
    }
};
