class Solution:
    def maxOperations(self, s: str) -> int:
        one_ind = []
        for i, c in enumerate(s):
            if c == '1':
                one_ind.append(i)
        one_ind.append(len(s))
        res, cur_count = 0, 0
        for i in range(len(one_ind)-1):
            cur_count += 1
            if one_ind[i+1] - one_ind[i] != 1:
                res += cur_count
        return res
