    int maxOperations(string s) {
        int ans=0;
        int cnt=s[0]-'0';
        int one = s[0]-'0';
        for(int i=1;i<s.size();i++){
          if(s[i-1]=='1' && s[i]=='0'){
             ans+=one;
             cout<<ans<<endl;
