class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int[] zeros = new int[n];
        int cnt = 0;
        for(int i=n-1;i>=0;){
            if(s.charAt(i)=='1'){
                zeros[i] = cnt;
                i--;
            }
            else{
                while(i>=0 && s.charAt(i)=='0'){
                    i--;
                }
                cnt++;
            }
        }
        int ans = 0;
        for(int i=0;i<n;i++){
            if(s.charAt(i)=='1'){
                ans+=zeros[i];
            }
        }
        return ans;
    }
}
