class Solution {
    public int maxOperations(String s) {
        int sum = 0;
        int res = 0;
        for (int i = 0;i<s.length();++i) {
            if (s.charAt(i) == '0') {
                while (i + 1 < s.length() && s.charAt(i + 1) == '0') {
                    ++i;
                }
                res += sum;
            } else {
                ++sum;
            }
        }
        return res;
    }
}
