class Solution {
public:
    int maxOperations(string s) {
        int c=0,zc=0,f=1;
        for(int i=s.length()-1;i>=0;i--){
            if(s[i]=='0'&&f){
                f=0;
                zc++;
            }
            else if(s[i]=='1'){
                f=1;
                c+=zc;
            }
        }
        return c;
    }
};
