#include <iostream>
#include <vector>
#include <string>
using namespace std;

class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, p = 0;
        bool k = 0;
        vector<int> h;

        // Loop through the string to collect 1s and 0s in h vector
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                h.push_back(1);
                k = 1;
            }
            if (s[i] == '0' && k == 1) {
                h.push_back(0);
                k = 0;
            }
        }

        // Count the operations needed
        for (auto it : h) {
            if (it == 1) {
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
